import React, { Component } from 'react'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { $ }  from 'react-jquery-plugin';
import { findDOMNode } from 'react-dom';


export default class product extends Component {
    render() {
			const settings = {
				dots: false,
				infinite: true,
				speed: 500,
				slidesToShow: 1,
				autoplay: true,
				arrows: false,
				slidesToScroll: 1,
				appendArrows: $('.news_arrow-left'),
				prevArrow: <div class="news__arrow news__arrow_dir_left"></div>,
        nextArrow: <div class="news__arrow news__arrow_dir_right"></div>
			};
			// var $status = $('.pagingInfo');
			// var $slickElement = $('.slideshow');
	
			// $slickElement.on('init reInit afterChange', function (event, slick, currentSlide, nextSlide) {
			// 		//currentSlide is undefined on init -- set it to 0 in this case (currentSlide is 0 based)
			// 		var i = (currentSlide ? currentSlide : 0) + 1;
			// 		$status.text(i + '/' + slick.slideCount);
			// });
        return (
					<>
						<div className="product_details-section">
							<div className="container product_desc-detail">
								<div className="product-content">
									<h5>the plaxonic difference</h5>
									<h1>Explore our Products</h1>
									<p>Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium totam</p>
								</div>
								<div className="product-slider">
									<Slider {...settings}>
										<div className="slider">
											<div className="slider__item">
											 <h3>1</h3>
											 <div className="news_arrow news__arrow_dir_right">

											 </div>
											</div>
										</div>
										<div className="slider">
											<div className="slider__item">
											 <h3>1</h3>
											</div>
										</div>
									</Slider>
								</div>
							</div>
						</div>
					</>
        )
    }
}
