import React, { Component } from 'react'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { $ }  from 'react-jquery-plugin';
import { findDOMNode } from 'react-dom';
import Next from '../../assests/next.svg'
import Previous from '../../assests/previous.svg'
import Product1 from '../../assests/Product1.jpg'
import Product2 from '../../assests/Product2.jpg'


const sliderElements = [
		
]
export default class product extends Component {
	
	constructor(props) {
    super(props);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
  }
  next() {
    this.slider.slickNext();
  }
  previous() {
    this.slider.slickPrev();
  }
	state = { currentSlide: 0 };

  handleAfterChange = (index) => {
    console.log("after change", index);
    this.setState({
      currentSlide: index
    });
  };
    render() {
			const settings = {
				dots: false,
				infinite: true,
				speed: 500,
				slidesToShow: 2,
				autoplay: false,
				arrows: false,
				slidesToScroll: 2,
				beforeChange: function (currentSlide, nextSlide) {
					console.log("before change", currentSlide, nextSlide);
				},
				afterChange: this.handleAfterChange
			};
        return (
					<>
						<div className="product_details-section  common-pad">
							<div className="container product_desc-detail">
								<div className="product-content">
									<h5>the plaxonic difference</h5>
									<h1>Explore our Products</h1>
									<p>Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium totam</p>
								</div>
								<div className="product-slider">
									<Slider ref={c => (this.slider = c)} {...settings}>
										<div className="slider">
											<div className="slider__item">
												<img src={Product1} alt="product-first"/>
												<div className="content-desc">
													<span>PlaxoLIMS</span>
													<p>Real-time Water Data Monitoring & Analysis</p>
												</div>
											</div>
										</div>
										<div className="slider">
											<div className="slider__item">
												<img src={Product2} alt="Product-sec"/>
												<div className="content-desc">
													<span>PlaxoSD</span>
													<p>Artificial Intelligence meets Industry 4.0</p>
												</div>
											</div>
										</div>
										<div className="slider">
											<div className="slider__item">
												<img src={Product2} alt="Product-sec"/>
												<div className="content-desc">
													<span>PlaxoSD</span>
													<p>Artificial Intelligence meets Industry 4.0</p>
												</div>
											</div>
										</div>
										<div className="slider">
											<div className="slider__item">
												<img src={Product2} alt="Product-sec"/>
												<div className="content-desc">
													<span>PlaxoSD</span>
													<p>Artificial Intelligence meets Industry 4.0</p>
												</div>
											</div>
										</div>
									</Slider>
									<div>
										{this.state.activeSlide}
										{this.state.activeSlide2}
									</div>
									<div style={{ textAlign: "right" }}>
										<button className="button back-btn" onClick={this.previous}>
											<img src= {Previous} alt= "Previous-btn"/>
										</button>
										<button className="button back-btn" onClick={this.next}>
										  <img src= {Next} alt= "Next-btn"/>
										</button>
									</div>
								</div>
							</div>
						</div>
					</>
        )
    }
}
